import React from 'react';
import { TextInput, StyleSheet } from 'react-native';

const StyledInput = ({props}) => {
    const styles = StyleSheet.create({
        input: {
            borderRadius: 5,
            borderWidth: 1,
            borderColor: "grey",
            paddingVertical: 10,
            paddingHorizontal: 20,
            marginBottom: 10,
        },
    });

    const styleInput = [
        ...styles.input,
        props.style,
    ]

    return (
        <TextInput style={styleInput} {...props}/>
    );
};

export default StyledInput;
