import { View } from "react-native-web";
import StyledText from "./StyledText";

const parseThousands = (number) => {
    return number > 1000 ? `${ Math.ro(number / 1000).toFixed(1)}k` : number;
}

const RepositoryStats = (props) => {
  return (
    <View style={{flexDirection: "row", justifyContent: "space-around"}}>
        <View>
            <StyledText align="center">{props.stargazersCount}</StyledText>
            <StyledText align="center" fontWeight="bold">STARS</StyledText>
        </View>
        <View>
            <StyledText align="center">{props.forksCount}</StyledText>
            <StyledText align="center" fontWeight="bold">FORKS</StyledText>
        </View>
        <View>
            <StyledText align="center">{props.reviewCount}</StyledText>
            <StyledText align="center" fontWeight="bold">REVIEWS</StyledText>
        </View>
        <View>
            <StyledText align="center">{props.ratingAverage}</StyledText>
            <StyledText align="center" fontWeight="bold">RATING</StyledText>
        </View>
    </View>
  );
};

const RepositoryItems = (props) => {
  return (
    <View key={props.id}>
      <StyledText fontWeight="bold" fontSize="subheading">
        {props.fullName}
      </StyledText>
      <StyledText primary>{props.description}</StyledText>
      <StyledText primary>LANGUAGE: {props.lenguage}</StyledText>
      <RepositoryStats {...props} />
    </View>
  );
};

export default RepositoryItems;
