        fontWeight === 'bold' && styles.bold,
        fontSize === 'subheading' && styles.subheading,
        align === 'center' && {textAlign: 'center'},
        colo
    ]


    return (
        <Text style={textStyles} {...restOfProps}>
            {children}
        </Text>
    );
};

export default StyledText;
